// Extract and prepare request data for agent
const body = $input.item.json.body;
const context = body.context || '';
const field = body.field || '';
const previousValues = body.previousValues || {};
const sessionId = body.sessionId || 'default-session';

// Simply pass the user's context as-is to allow natural conversation
let chatInput = context;

// Add project information only on first message
if (Object.keys(previousValues).length > 0) {
  chatInput += '\n\nمعلومات المشروع:\n';
  for (const [key, value] of Object.entries(previousValues)) {
    if (value) {
      chatInput += `- ${key}: ${value}\n`;
    }
  }
}

return {
  json: {
    chatInput: chatInput,
    sessionId: sessionId,
    originalContext: context,
    field: field,
    previousValues: previousValues
  }
};

// Extract agent response
const agentOutput = $input.item.json;
const preparedInput = $('Prepare Agent Input').item.json;

// Get the suggestion text from agent output
let suggestion = '';
if (typeof agentOutput.output === 'string') {
  suggestion = agentOutput.output.trim();
} else if (agentOutput.text) {
  suggestion = agentOutput.text.trim();
} else {
  suggestion = JSON.stringify(agentOutput);
}

// Clean up common unwanted prefixes and formatting
const prefixesToRemove = [
  'Description:',
  'وصف:',
  '"',
  "'",
  'Task Description:',
  'وصف المهمة:',
  'Here is',
  'Here\'s',
  'إليك',
  'الوصف:'
];

for (const prefix of prefixesToRemove) {
  if (suggestion.toLowerCase().startsWith(prefix.toLowerCase())) {
    suggestion = suggestion.substring(prefix.length).trim();
  }
}

// Remove trailing quotes
if (suggestion.endsWith('"') || suggestion.endsWith("'")) {
  suggestion = suggestion.slice(0, -1).trim();
}
if (suggestion.startsWith('"') || suggestion.startsWith("'")) {
  suggestion = suggestion.substring(1).trim();
}

// Format response for frontend
return {
  json: {
    success: true,
    data: {
      suggestion: suggestion,
      confidence: 0.9,
      metadata: {
        field: preparedInput.field,
        context: preparedInput.originalContext,
        model: 'llama3.1:8b',
        agentType: 'langchain-agent-with-memory',
        sessionId: preparedInput.sessionId,
        timestamp: new Date().toISOString()
      }
    },
    message: 'Suggestion generated successfully with AI Agent'
  }
};

// Handle errors gracefully
const error = $input.item.json.error || $input.item.json;

return {
  json: {
    success: false,
    data: null,
    message: error.message || 'Failed to generate suggestion with agent',
    error: {
      type: error.name || 'AgentError',
      details: error.stack || JSON.stringify(error)
    }
  }
};
